import React from 'react';
import Avatar from './Avatar';

const App: React.FC = () => {
  // Define el objeto con la información del avatar
  const avatarInfo = {
    imageUrl: 'https://res.cloudinary.com/dcba9lt2s/image/upload/v1702480669/pa7hlf3notzhjuuohmis.png',
    altText: 'Gregorio Y. Zara',
    className: 'avatar',
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <div>
        <h1>¡Mi componente Avatar dinámico!</h1>
        {/* Pasa el objeto como propiedades a Avatar */}
        <Avatar avatarInfo={avatarInfo} />
      </div>
    </div>
  );
};

export default App;
