// Avatar.tsx
import React from 'react';

interface AvatarProps {
  avatarInfo: {
    imageUrl: string;
    altText: string;
    className: string;
    imageSize?: string; // Nueva propiedad opcional para el tamaño de la imagen
  };
}

const Avatar: React.FC<AvatarProps> = ({ avatarInfo }) => {
  const { imageUrl, altText, className, imageSize } = avatarInfo;

  return (
    <img
      className={className}
      src={imageUrl}
      alt={altText}
      style={{ width: imageSize, height: imageSize }} // Aplicar el tamaño de la imagen si está definido
    />
  );
};

export default Avatar;
